$(document).ready(function(){xcloud.init()});var xcloud=new function(){this.saveMimeId=false;this.init=function(){rcmail.addEventListener("beforemenu-open",function(p){if(p.menu=="attachmentmenu"){xcloud.saveMimeId=p.id;for(var plugin in rcmail.env.xcloud_plugins){insertMenuAttachmentSaveCode(plugin,p.id)}}});for(var plugin in rcmail.env.xcloud_plugins){insertImageAttachmentSaveCode(plugin)}var width=0;$("#compose-attachments input[type=button]").each(function(){if($(this).outerWidth()>width){width=$(this).outerWidth()}});$("#compose-attachments input[type=button]").width(width)};this.selectSuccess=function(data,linkType,plugin,parameters){if(linkType=="preview"){selectPreviewSuccess(data,plugin,parameters)}else{selectDirectSuccess(data,plugin,parameters)}};var insertImageAttachmentSaveCode=function(plugin){if(!$("#aria-label-messageattachments").length||typeof window[plugin]["insertImageSaveButton"]!=="function"){return}$("p.image-attachment").each(function(){var container=$(this).find(".xcloud-save-button-container");if(!container.length){container=$("<div>").addClass("xcloud-save-button-container").appendTo($(this).find("span.attachment-links"))}var name=$(this).find(".image-filename").text();var url=false;$(this).find("span.attachment-links > a").each(function(){var href=$(this).attr("href");if(href.indexOf("_download=1")!=-1){url=href}});if(!name||!url){return true}var button=window[plugin]["insertImageSaveButton"](name,url);if(button){$("<div>").addClass(plugin+"-save-button-wrap xcloud-save-button-wrap").append(button).appendTo(container)}})};var insertMenuAttachmentSaveCode=function(plugin,id){if(!$("#aria-label-messageattachments").length||typeof window[plugin]["insertMenuSaveButton"]!=="function"){return}if(!$("#"+plugin+"-attachment-menu").length){$("<li>").addClass("xcloud-attach-menu-container").append($("<div>").attr("id",plugin+"-attachment-menu").addClass("active")).appendTo($("#attachmentmenu ul"))}var a=$("#attach"+id+" a").first();var url=a.attr("href");var copy=a.clone();copy.find("span").remove();var name=copy.text().trim();if(!name||!url){return}window[plugin]["insertMenuSaveButton"](id,name,url+"&download=1")};var selectPreviewSuccess=function(data,plugin,parameters){var html=$("input[name='_is_html']").val()=="1";var links=[];for(var key in data){if(data.hasOwnProperty(key)){if(html){links.push("<a class='xcloud-link "+plugin+"-link' href='"+data[key]["url"]+"'>"+data[key]["name"]+"</a>")}else{links.push(data[key]["url"])}}}if(html){tinyMCE.execCommand("mceInsertContent",false," "+links.join(", ")+" ")}else{var element=$("#composebody");var value=element.val();element.val(value.substring(0,element.prop("selectionStart"))+"\n\n"+links.join("\n")+"\n\n"+value.substring(element.prop("selectionEnd"),0))}};var selectDirectSuccess=function(data,plugin,parameters){var uploadId=new Date().getTime();rcmail.add2attachment_list(uploadId,{html:"<span>Uploading</span>",classname:"uploading",complete:false});var send={files:data,uploadId:uploadId,composeId:rcmail.env.compose_id};if(typeof parameters==="object"){for(var name in parameters){send[name]=parameters[name]}}rcmail.http_post("plugin."+plugin+"_attach",send,rcmail.set_busy(true,"uploading"))};this.saveAttachmentDeployFile=function(mimeId){if(typeof mimeId==="undefined"||!mimeId){mimeId=this.saveMimeId}var data={code:xframework.getRandomCode(),name:$("#attachment-list #attach"+mimeId).find("a:not(.drop)").clone().children().remove().end().text().trim()};$.ajax({url:rcmail.url("SaveAttachmentDeployFile"),method:"POST",headers:{"x-csrf-token":rcmail.env.request_token},dataType:"json",data:{messageId:rcmail.env.uid,mbox:rcmail.env.mailbox,mimeId:mimeId,code:data.code}});return data};this.removeAttachmentDeployFile=function(code){$.ajax({url:rcmail.url("RemoveAttachmentDeployFile"),method:"POST",headers:{"x-csrf-token":rcmail.env.request_token},dataType:"json",data:{code:code}})}};